import { Button } from "react-weui";
import Lang from "../../lang/Lang";
import React from "react";
import {IoIosStar, IoIosCheckmarkCircleOutline} from "react-icons/io";
import {Config} from "../../Config";
import AuthService from "../../service/AuthService";
import Modal from 'react-modal';
import CompanyService from "../../service/CompanyService";



class SubscribeBtn extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            showModal: false
        }
    }
    componentDidMount(props) {
        this.getQrCode(this.props.item, this.props.company)
        // this.setState({
        //     company: this.props.company,
        //     item: this.props.item,
        // }, () => {
        //     WeChatService.loadWeChatJsConf(window.location.href);
        // });
    }

    componentWillReceiveProps(nextProps, nextContext) {
        // this.setState({
        //     item: nextProps.item,
        // }, () => {
        //     WeChatService.loadWeChatJsConf(window.location.href);
        // });
    }

    subscribe = (company) => {
        let user = JSON.parse(localStorage.getItem('userObject'));
        user = {
            wechat_is_subscriber: true
        }
        if (!user) {
            this.weChatLogin()
        } else {
            if (user.wechat_is_subscriber) {
                // subscribe actions
                CompanyService.subscribeCompany(company.id)
                    .then(res => {
                        console.log(res)
                        if(res.status == 200) {
                            this.setState({
                                success: true
                            })
                        } else {
                            this.setState({
                                alert: true
                            })
                        }
                    })
                ;
            } else {
                this.setState({
                    showModal: true
                })
            }
        }
    }

    weChatLogin() {
        let origUrl = window.location.href;
        let authUrl = Config.webAddress +'AuthPage';
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }

    getQrCode(item, company) {
        let params = {};
        if (item && item.id) {
            params = {
                itemId: item.id
            }
        }
        let companyId = company.id
        CompanyService.getCompanyQrCode(companyId, params)
            .then(response => response.text())
            .then(response => {
                this.setState({
                    qrUrl: response
                })
            })
    }

    render(){
        const customStyles = {
            content : {
                top                   : '50%',
                left                  : '50%',
                right                 : 'auto',
                bottom                : 'auto',
                marginRight           : '-50%',
                transform             : 'translate(-50%, -50%)'
            }
        };
        // const company = this.props.company;

        return (
            <div>
                <Button
                    size='small'
                    style={styles.subsButton}
                    onClick={()=>{this.subscribe(this.props.company)}}
                    plain
                >
                    <IoIosStar/>
                    {Lang.translate('SUBSCRIBE')}
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    // onAfterOpen={() => {this.getQrCode(item)}}
                    onRequestClose={()=>{this.setState({showModal: false})}}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                        <div style={styles.qrTitle}>扫码关注该公司</div>
                        <div style={styles.qrTitle}><small>将更及时的收到推送</small></div>
                        <img style={styles.qrcode} src={this.state.qrUrl}/>
                </Modal>
                <Modal
                    isOpen={this.state.success}
                    // onAfterOpen={() => {this.getQrCode(item)}}
                    onRequestClose={()=>{this.setState({success: false})}}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <IoIosCheckmarkCircleOutline/>
                    <h3 className="text-success">Success</h3>
                </Modal>
                <Modal
                    isOpen={this.state.alert}
                    // onAfterOpen={() => {this.getQrCode(item)}}
                    onRequestClose={()=>{this.setState({alert: false})}}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h3 className="text-warning">You have subscribed this company.</h3>
                </Modal>
            </div>

        );
    }
}

const styles = {
    subsButton: {
        borderColor: 'red',
        color: 'red'
        // padding: '2vw',
    },
    qrcode: {
        width: '100%',
    },
    qrTitle: {
        width: '100%',
        textAlign: 'center'
    },
};


export default SubscribeBtn;
