import React, { Component } from 'react';
import './App.css';
import { Toast} from'react-weui';
import Router from 'next/router';

//import component
import SiteHeader from "../components/Common/SiteHeader";
import AuthService from '../service/AuthService';


class AuthPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            showToast: true,
        };
    }

    componentDidMount() {
        localStorage.setItem('userToken', this.props.url.query.token);
        localStorage.setItem('authString', this.props.url.query.authString);
        localStorage.setItem('isLogIn', true);
        this.getMe();

        // Router.push({
        //     pathname: '/itemDetails',
        //     // query: {itemId: this.props.url.query.origUrl},
        // });
        window.open(this.props.url.query.origUrl,"_self");
    }

    getMe() {
        console.log(localStorage.getItem('userToken'));
        let params ={};
        AuthService.getMe(params)
            .then((response) => response.json())
            .then((responseJson) => {
                console.log(responseJson);
                localStorage.setItem('userObject', responseJson);
            })
            // .then(() => {
            //     alert(this.props.url.query.origUrl);
            //     window.open('www.google.com');
            // })
            .catch((error) => {
                console.error(error);
            });
    }


    render() {
        return (
            <div className="App-body">
                <SiteHeader/>
                <div className='App-body'>
                    <Toast icon="loading" show={this.state.showToast}>微信登录中...</Toast>
                </div>

            </div>

        );
    }
}

export default AuthPage;
