import React from 'react';
import Lang from '../../lang/Lang';
import Colors from "../../constants/Colors";
import {MdDelete} from "react-icons/md";
import {IoIosTrash} from "react-icons/io";


class CartClearSection extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            clearCart: this.props.clearCart,
        };
    }

    clearCartItems() {
        alert('1');
        this.state.clearCart([]);
    }

    render(){
        return (
            <div style={styles.wrapper}>
                <div style={styles.subWrapper}>
                    <div style={styles.title}>{Lang.translate('CART.ITEM')}</div>
                </div>
                <div>
                    <span style={styles.clearButton} onClick={()=>this.clearCartItems()}>
                        <IoIosTrash/> <span style={{fontSize: 17}}>{Lang.translate('CART.CLEAR')}</span>
                    </span>
                </div>
            </div>
        );
    }
}

const styles = {
    wrapper: {
        display: "flex",
        flex: "1",
        flexDirection: "row",
        backgroundColor: 'white',
        padding: '15px',
    },
    subWrapper: {
        flex: '1',
    },
    title: {
        fontSize:'17px',
        color: '#797979',
        marginBottom: '-6px',
    },
    clearButton: {
        fontSize:'15px',
        color: '#797979',
        textAlign:'right',
        marginBottom: '-2px'
    }
};


export default CartClearSection;
