import React, {Component} from 'react';
import Colors from "../../../constants/Colors";
import ArticleContent from "./ArticleContent";
import ArticleComments from "./ArticleComments";


class ArticleDetailComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLogin: this.props.isLogin,
            item: this.props.item
        };
    }

    componentDidMount() {

    }


    render(){
        const item = this.state.item ? this.state.item : undefined;
        const isLogin = this.state.isLogIn;
        console.log(item.is_draft);
        console.log(isLogin);

        if(item.is_draft)
            return(
                <div key={item.id}>
                    <ArticleContent articleDetail={item}/>
                    <ArticleComments item={item}/>
                </div>
            )

    }
}

const styles = {

};

export default ArticleDetailComponent;
