import React, {Component} from 'react';

class ItemContent extends Component {
    constructor(props){
        super(props);
    }

    formatRawContent = (content => {
        return content.replace(/<img([^>]*)\ssrc=['"]([^"\s]+)['"]([^>]*)>/g, "<img width=\"100%\" src='$2'/>");
    });

    render(){
        const jieLongDetail = this.props.jieLongDetail ? this.props.jieLongDetail : undefined;
        const formattedContent = jieLongDetail && jieLongDetail.description ? this.formatRawContent(jieLongDetail.description) : '';
        if (jieLongDetail) {
            return (
                <div style={styles.contentWrapper}>
                    <div style={styles.productInfo}>
                        <div style={styles.dayCount}></div>
                        <div style={styles.visitTimes}>
                            访问次数<br/>{jieLongDetail.visit_times}
                        </div>
                    </div>
                    <div style={styles.descriptionWrapper}>
                        <h3>{jieLongDetail.title}</h3>
                        <div style={styles.descriptionBackground} dangerouslySetInnerHTML={{__html: formattedContent}}></div>
                    </div>
                </div>
            );
        }
        else return null;
    }
}

const styles = {
    contentWrapper: {
      backgroundColor: '#fff'
    },
    productInfo: {
        fontSize: '12px',
        display: 'flex',
        padding: ' 2vmin 4vmin',

        flex: '0.5'
    },
    dayCount: {
        flex: 1
    },
    visitTimes: {
        flex: 1,
        textAlign: 'end',
    },
    descriptionWrapper: {
        padding: '4vmin',
        paddingTop: 0,
    },
    descriptionBackground: {
        backgroundColor: '#fff'
    }

};

export default ItemContent;
