import React, {Component} from 'react';
import Layout from '../../constants/Layout';
import Link from 'next/link';

class ProductCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            product: this.props.product,
            defaultImage: '../static/assets/default_item.png'
        };
    }

    render(){
        const product = this.props.product;
        const imageList = product.images_in_diff_sizes && product.images_in_diff_sizes.smallImages.length !== 0 ? product.images_in_diff_sizes.smallImages : product.images_in_diff_sizes.origImages;
        const imageArray = Object.keys(imageList).map(i => imageList[i]);
        const imageUrl = imageArray[0] ? imageArray[0].url : this.state.defaultImage;
        return (
            <Link href={{ pathname: '/itemDetails', query: { itemId: product.id} }}>
                <a>
                    <img style={styles.img} src={imageUrl}/>
                    <div style={styles.productTitle}>{product.title}</div>
                </a>
            </Link>
        );
    }
}

const styles = {
    productTitle: {
        padding: '5%',
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        whiteSpace: 'nowrap',
        width: '90%',
        fontSize: '12px',
        color: '#000'
    },
    img: {
        width: '100%',
        height: '30vw',
        objectFit: 'cover'
    },
};

export default ProductCard;
