import React, {Component} from 'react';
import Colors from "../../../constants/Colors";
import RegistrationFields from "./RegistrationFields";
import ProductButtonArea from "./ProductButtonArea";
import RegistrationVoucherComponent from "./RegisterationVoucherComponent"
import ItemBanner from "../ItemBanner";
import ItemContent from "../ItemContent";


class RegistrationDetailComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLogin: this.props.isLogin,
            item: this.props.item,
            formValues: {}
        };
    }

    showHideCheckoutView = () => {
        this.setState((prevState) => ({
            isCheckingOut: !prevState.isCheckingOut
        }));
    };

    updateFormValues = formValues => {
        this.setState({
            formValues: formValues
        }, () => {
            console.log(this.state.formValues)
        })
    };

    render(){
        const { item, isLogin } = this.state
        return (
            <div className='App-body' key={ item.id } style={ styles.pageBackground }>
                <ItemBanner item={ item }/>

                <ItemContent itemDetail={ item ? item : undefined } showDivider={ false }/>

                <RegistrationVoucherComponent item={ item } isLogin={isLogin}/>

                <RegistrationFields item={ item } callbackUpdateForm={this.updateFormValues}/>

                <ProductButtonArea item={ item } isLogin={ isLogin } selectedList={ this.state.selectedList } callbackChangeView={ this.showHideCheckoutView }/>
            </div>

        )
    }
}

const styles = {
    pageBackground: {
        backgroundColor: Colors.formBackground
    }
};

export default RegistrationDetailComponent;
