import React from 'react';
import Lang from '../../lang/Lang';
import Head from "../head";
import Router from "next/dist/client/router";
import ItemService from "../../service/ItemService";
import CartService from "../../service/CartService";
import CartListItem from "../../components/Cart/CartListItem";
import ConsultationListItem from "../Company/ConsultationList/ConsultationListItem";
import {Button, LoadMore} from "react-weui";

class CartList extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            companyId: this.props.companyId,
        };
    }

    componentDidMount() {
        this.getItems();
        this.getCartItems();
    }



    getCartItems() {
        let companyId = '8e26b6edb2ce7416f71a82ed78e08984';
        CartService.getCartItems(companyId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    cartItems: responseJson
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    getItems() {
        let params = this.state.companyId;
        ItemService.getItemDetail(params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    itemList: responseJson.children
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    render(){
        const itemList = this.state.itemList;
        const cartItems = this.state.cartItems;
        console.log(itemList);
        console.log(cartItems);


        if (itemList) {
            return (
                <div style={styles.listWrapper}>
                    {
                        itemList.map(function(item,index){
                            return (<CartListItem item={item} key={index}/>)})
                    }
                </div>
            );
        }
        else return null;
    }
}

const styles = {
    listWrapper: {
        width: '100%'
    },
};


export default CartList;
