import React, { Component } from 'react'
import Colors from "../../../constants/Colors"
import { Button, TextArea, Radio, Cells, CellsTitle, Cell, CellHeader, CellBody, CellFooter, Form, FormCell,  Label } from 'react-weui'
import Input from "../../../components/Common/form/Input"

class RegistrationFields extends Component {
    constructor(props){
        super(props)
        this.state = {
            callbackUpdateForm: this.props.callbackUpdateForm,
            config: this.props.item.config,
            isParticipated: this.props.isParticipated,
            formValues: {},
            formArray: []
        }
    }

    componentDidMount() {
        this.extractFieldsFromConfig()
    }

    componentWillReceiveProps(nextProps, nextContext) {
        this.setState({
            isParticipated: nextProps.isParticipated
        })
    }

    initFormTemplate = () => {
        let tmpConfig = JSON.parse(this.state.config)
        let tmpFormValues = []
        for (let field of tmpConfig.inputFields) {
            tmpFormValues.push({
                'title': field.title,
                'value': ''
            })
        }
        this.setState({
            formValues: tmpFormValues
        })
    }

    extractFieldsFromConfig = () => {
        let tmpConfig = JSON.parse(this.state.config)
        this.setState({
            inputFields: tmpConfig.inputFields && tmpConfig.inputFields.length > 0 ? tmpConfig.inputFields : undefined
        }, ()=> {
            this.initFormTemplate()
        })
    }

    handleChange = event => {
        let { formValues } = this.state
        for (let formValue of formValues) {
            if (formValue.title === event.target.name) {
                formValue.value = event.target.value
            }
        }
        this.setState({
            formArray: formValues
        }, ()=>{
            this.state.callbackUpdateForm(this.state.formArray)
        })
    }


    render(){
        const { inputFields, isParticipated } = this.state
        if (inputFields && !isParticipated) {
            return (
                <div style={styles.formWrapper}>
                    <form>
                        <Input label="test field" errorMessage="error!!!" required={true} />
                    </form>
                    {
                        inputFields.map((field, index) => {
                            let { type, title } = field
                            return (
                                <div key={ index } >
                                    <CellsTitle>{ field.title }</CellsTitle>
                                    <Form>
                                        <FormCell>
                                            <CellBody>
                                                {
                                                    type === 'input' ?
                                                        <input
                                                            type="text"
                                                            name={ title }
                                                            onChange={ this.handleChange }
                                                            placeholder={ '请输入' + title }
                                                            style={ styles.inputField } />
                                                        :
                                                        <TextArea rows="3"
                                                                  name={ title }
                                                                  style={ styles.inputField }
                                                                  onChange={ this.handleChange }/>
                                                }
                                            </CellBody>
                                        </FormCell>
                                    </Form>
                                </div>
                            )
                        })
                    }
                </div>
            )
        }
        else return null
    }
}

const styles = {
    formWrapper: {
        backgroundColor: Colors.formBackground
    },
    inputField: {
        fontSize: '14px'
    }
}

export default RegistrationFields
