import React, {Component} from 'react';
import {Button} from 'react-weui';
import {Config} from "../../../Config";
import Lang from "../../../lang/Lang";


class EditButton extends Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
        };
    }

    redirect(){
        let url = Config.showmineAngular.webAddress + Config.showmineAngular.itemDetails + this.state.item.id;
        window.open(url,"_self");
    }

    render(){
        const item = this.state.item ? this.state.item : undefined;
        const show = this.props.show !== false;
        if (show) {
            return (
                <div key={item.id} style={styles.editButton}>
                    <Button
                        size='small'
                        onClick={()=>{this.redirect()}}
                        plain
                    >
                        {Lang.translate('EDIT')}
                    </Button>
                </div>
            )
        }
        else return null;
    }
}

const styles = {
    editButton: {
        padding: '2vw',
        paddingLeft: '4vw',
    }

};

export default EditButton;
