import { Button } from "react-weui";
import Lang from "../../lang/Lang";
import React from "react";
import {IoIosStar} from "react-icons/io";
import {Config} from "../../Config";
import AuthService from "../../service/AuthService";
import Modal from 'react-modal';



class SubscribeBtn extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            showModal: false
        }
    }
    componentDidMount(props) {
        // this.setState({
        //     company: this.props.company,
        //     item: this.props.item,
        // }, () => {
        //     WeChatService.loadWeChatJsConf(window.location.href);
        // });
    }

    componentWillReceiveProps(nextProps, nextContext) {
        // this.setState({
        //     item: nextProps.item,
        // }, () => {
        //     WeChatService.loadWeChatJsConf(window.location.href);
        // });
    }
    subscribe = () => {
        let user = JSON.parse(localStorage.getItem('userObject'));
        user = {
            wechat_is_subscriber: false
        }
        if (!user) {
            this.weChatLogin()
        } else {
            if (user.wechat_is_subscriber) {
                // subscribe actions
            } else {
                this.setState({
                    showModal: true
                })
            }
        }
       // no user
        // has user no subscribe
        // has user with subs
    }

    weChatLogin() {
        let origUrl = window.location.href;
        let authUrl = Config.webAddress +'AuthPage';
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }


    render(){
        const item = this.props.item ;
        const customStyles = {
            content : {
                top                   : '50%',
                left                  : '50%',
                right                 : 'auto',
                bottom                : 'auto',
                marginRight           : '-50%',
                transform             : 'translate(-50%, -50%)'
            }
        };
        // const company = this.props.company;

        return (
            <div key={item.id}>
                <Button
                    size='small'
                    style={styles.subsButton}
                    onClick={()=>{this.subscribe()}}
                    plain
                >
                    <IoIosStar/>
                    {Lang.translate('SUBSCRIBE')}
                </Button>
                <Modal
                    isOpen={this.state.showModal}
                    // onAfterOpen={this.afterOpenModal}
                    onRequestClose={()=>{this.setState({showModal: false})}}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                </Modal>
            </div>

        );
    }
}

const styles = {
    subsButton: {
        borderColor: 'red',
        color: 'red'
        // padding: '2vw',
    }
};


export default SubscribeBtn;
