import React, {Component} from 'react';
import Colors from '../../../constants/Colors';
import { LoadMore } from 'react-weui';
import Link from 'next/link';
import moment from 'moment';


class ArticleContent extends Component {
    constructor(props){
        super(props);
    }

    formatRawContent = (content => {
        return content.replace(/<img([^>]*)\ssrc=['"]([^"\s]+)['"]([^>]*)>/g, "<img width=\"100%\" src='$2'/>");
    });

    render(){
        const articleDetail = this.props.articleDetail ? this.props.articleDetail : undefined;
        const formattedConfig = articleDetail && articleDetail.config ? JSON.parse(articleDetail.config) : {};
        const author = articleDetail.author && articleDetail.author > '' ? articleDetail.author : undefined;
        const writer = formattedConfig .writer && formattedConfig .writer > '' ? formattedConfig .writer : undefined;
        const formattedContent = articleDetail && articleDetail.description ? this.formatRawContent(articleDetail.description) : '';
        const localDateCreated = moment(moment.utc(articleDetail.date_publish)).local().format("YYYY-MM-DD HH:mm");

        if (articleDetail) {
            return (
                <div style={styles.contentWrapper}>
                    <div style={styles.descriptionWrapper}>
                        <h2 style={styles.articleTitle}>{articleDetail.title}</h2>

                        <div style={styles.subTitle}>
                            { author ? <span style={styles.author}>{author}</span> : null }
                            <Link href={{ pathname: '/companyDetails', query: { companyId: articleDetail.company_id} }}>
                                <a>
                                    <span style={styles.companyName}>{articleDetail.company.name}</span>
                                </a>
                            </Link>
                            <span style={styles.publishDate}>{localDateCreated}</span>
                        </div>

                        <div style={styles.descriptionBackground} dangerouslySetInnerHTML={{__html: formattedContent}}/>

                    </div>
                    <LoadMore showLine> The End </LoadMore>

                    <div style={styles.productInfo}>
                        <div style={styles.visitTimes}>阅读 {articleDetail.visit_times}</div>
                        { writer ? <div style={styles.writer}>作者： {writer}</div> : null }
                    </div>
                </div>
            );
        }
        else return null;
    }
}

const styles = {
    contentWrapper: {
        backgroundColor: '#fff',
        color: Colors.fontColor,
        padding: '4vmin'
    },
    descriptionWrapper: {
    },
    articleTitle: {
        fontSize: '22px'
    },
    subTitle: {
        paddingTop: '2vw',
        paddingBottom: '2vw',
        fontSize: '14px'
    },
    author: {
        color: Colors.fontColorGray,
        paddingRight: '3vw'
    },
    companyName: {
        color: Colors.fontColorLink,
        paddingRight: '3vw'
    },
    publishDate: {
        color: Colors.fontColorGray
    },
    descriptionBackground: {
        backgroundColor: '#fff',
        // padding: '2.5vw',
        // paddingBottom: '0',
        lineHeight: '2',
        fontSize: '95%'
    },
    productInfo: {
        display: 'flex',
        color: Colors.fontColorGray,
        fontSize: '14px'
    },
    visitTimes: {
        flex: 1,
        textAlign: 'start',
    },
    writer: {
        flex: 3,
        textAlign: 'end'
    }

};

export default ArticleContent;
