import React, {Component} from 'react';
import Iframe from 'react-iframe';
import {Button, Dialog, LoadMore, Toast} from "react-weui";
import Colors from "../constants/Colors";

class Btn extends Component {
    constructor(props){
        super(props);

        this.state = {
            style: this.props.style,
            disabled: this.props.disabled,
            loading: this.props.loading,
            title: this.props.title,
            onClick: this.props.onClick,
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            style: object.style,
            disabled: object.disabled,
            loading: object.loading,
            title: object.title,
            onClick: object.onClick,
        })
    }

    render(){
        return (
            <div style={styles.dialog}>
                <Button style={styles.btn} onClick={this.state.onClick} disabled={this.state.disabled}>
                    {this.state.loading ? <LoadMore loading/> : this.state.title}
                </Button>
                <Toast icon="loading" show={this.state.loading}>请稍后...</Toast>
            </div>
        );
    }
}

const styles = {
    btn: {
        backgroundColor: Colors.appThemeColor,
        color: Colors.fontColor,
        fontWeight: 'bold',
        width:'100%',
        height:'10vw',
        fontSize:'18px',
        flex:'1',
    },
    dialog: {
        width:'100%',
        height:'10vw',
        // backgroundColor: 'red'
    }
};

export default Btn;
