import React from 'react';
import Lang from '../../lang/Lang';
import Colors from "../../constants/Colors";
import {IoIosTrash} from "react-icons/io";

import CartService from "../../service/CartService"

class CartClearSection extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            callbackClearCart : this.props.callbackClearCart,
            companyId: this.props.companyId
        };
    }

    clearCartItems = () => {
        CartService.clearCartItems (this.state.companyId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.state.callbackClearCart();
                console.log(responseJson);
            })
            .catch((error) => {
                console.error(error);
            });
    }

    render(){
        return (
            <div style={styles.wrapper}>
                <div style={styles.subWrapper}>
                    <div style={styles.title}>{Lang.translate('CART.ITEM')}</div>
                </div>
                <div>
                    <span style={styles.clearButton} onClick={()=>{this.clearCartItems()}}>
                        <IoIosTrash/> <span style={{fontSize: 17}}>{Lang.translate('CART.CLEAR')}</span>
                    </span>
                </div>
            </div>
        );
    }
}

const styles = {
    wrapper: {
        display: "flex",
        flex: "1",
        flexDirection: "row",
        backgroundColor: 'white',
        padding: '15px',
    },
    subWrapper: {
        flex: '1',
    },
    title: {
        fontSize:'17px',
        color: '#797979',
        marginBottom: '-6px',
    },
    clearButton: {
        fontSize:'15px',
        color: '#797979',
        textAlign:'right',
        marginBottom: '-2px'
    }
};


export default CartClearSection;
