import React from 'react';
import Router from "next/dist/client/router";
import Colors from "../../constants/Colors";


class CartListItem extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            defaultImage: '../static/assets/default_item.png',
            quantity: 0,
            price: this.props.item.item.price,
            totalPrice: 0,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({quantity: Number(event.target.value)});
        this.handleTotalPrice();
    }

    handleQuantity(params) {
        if(params == 'plus') {
            this.state.quantity = this.state.quantity + 1;
            this.setState({
                quantity: this.state.quantity,
            })
        }
        else {
            if(this.state.quantity < 1) {
                console.log('cannot minus');
            }
            else {
                this.state.quantity = this.state.quantity - 1;
                this.setState({
                    quantity: this.state.quantity,
                })
            }
        }
        this.props.myCallBack(this.state.quantity);
        this.handleTotalPrice();
    }

    handleTotalPrice() {
        this.state.totalPrice = this.state.quantity * this.state.price;
        this.setState({totalPrice: this.state.totalPrice})
    }

    callBack() {
        let listInfo = {itemId: this.state.item.id, qty: this.state.quantity, totalPrice: this.state.totalPrice}
        this.props.callbackFromParent(listInfo);
    }

    render(){
        const item = this.state.item.item;
        const imageList = item.images_in_diff_sizes && item.images_in_diff_sizes.smallImages.length !== 0 ? item.images_in_diff_sizes.smallImages : item.images_in_diff_sizes.origImages;
        const imageArray = Object.keys(imageList).map(i => imageList[i]);
        const imageUrl = imageArray[0] ? imageArray[0].url : this.state.defaultImage;

        return (
            <div style={{backgroundColor:'white'}}>
                <div style={styles.itemWrapper}>
                    <div style={styles.imgWrapper}>
                        <img style={styles.img} src={imageUrl}/>
                    </div>
                    <div style={styles.contentWrapper}>
                        <div style={styles.itemTitle}>{item.title}</div>
                        <div style={styles.itemPrice}>${item.price}</div>
                        <div className="Number-Picker">
                            <button style={styles.pickerButton} onClick={() => {this.handleQuantity('minus');}}> － </button>
                            <input style={styles.picker} type="number" min="0" max="1000"
                                   value={this.state.quantity}
                                   onChange={this.handleChange}
                            />
                            <button style={styles.pickerButton} onClick={() => {this.handleQuantity('plus');}}> ＋ </button>
                        </div>
                    </div>
                </div>
                <div style={styles.bottomDivider}> </div>
            </div>


        );
    }
}

const styles = {
    itemWrapper: {
        flexDirection: 'row',
        flex: '1',
        display:'flex',
        borderBottom: '1px solid',
        borderColor: Colors.borderColor,
        padding: '15px',
    },
    imgWrapper: {
        flex: "1",
    },
    contentWrapper: {
        flex: "1",
        alignItems:'-end',
    },
    itemTitle: {
        fontSize: '18px',
        textAlign: 'right',
    },
    itemPrice: {
        color: 'red',
        fontSize: '18px',
        textAlign: 'right',
    },
    picker:{
        textAlign: 'center',
        fontSize: '18px',
        height: '28px',
        width: '60px',
        borderRadius: '0',
        border: 'none',
    },
    pickerButton: {
        fontSize: '18px',
        backgroundColor: '#efc929',
        border: 'none',
        width: '28px',
        height: '28px',
        borderRadius: '0',
    },
    bottomDivider: {
        clear: 'both',
        padding: '3px 0',
        backgroundColor: Colors.borderColor,
        display: 'block'
    },
    img: {
        width: '100%',
        height: '30vw',
        objectFit: 'cover'
    },
};


export default CartListItem;
