import React, {Component} from 'react';
import Link from 'next/link';
import Colors from '../../../constants/Colors';

class ConsultationListItem extends Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            defaultImage: '../../../images/default_item.png'
        };
    }

    render(){
        const item = this.props.item;
        console.log(item);
        const imageArray = Object.keys(item.images_in_diff_sizes.smallImages).map(i => item.images_in_diff_sizes.smallImages[i]);
        const imageUrl = item && item.images_in_diff_sizes && item.images_in_diff_sizes.smallImages && imageArray[0].url ? imageArray[0].url : this.state.defaultImage;
        // const imageUrl = item && item.images && item.images[0] && item.images[0].url ? item.images[0].url : this.state.defaultImage;
        return (
            <div style={styles.cardWrapper}>
                <div style={styles.imageWrapper}>
                    <img style={styles.img} src={imageUrl}/>
                </div>
                <div style={styles.textContainer}>
                    <p style={styles.itemTitle} className={'truncate'}>{item.title}</p>
                    <p style={styles.itemTime}>{item.date_publish}</p>
                </div>
            </div>
        );
    }
}

const styles = {
    img: {
        width: '100%',
        height: '22vw',
        objectFit: 'cover'
    },
    cardWrapper: {
        width: '100%',
        display: 'flex',
        borderBottom: '2px solid',
        borderColor: Colors.borderColor,
        marginBottom: '10px',
        paddingBottom: '5px'
    },
    imageWrapper: {
        flex: '8',
        paddingLeft:'5px'
    },
    textContainer: {
        flex: '13',
        padding: '0px 10px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'stretch',
        justifyContent: 'center',
    },
    itemTitle: {
        fontSize: '17px',
        color: Colors.fontColor,
        fontWeight: 'bold',
        flex: '4'
    },
    itemTime: {
        fontSize: '12px',
        color: '#828a92',
        flex: '3',
        paddingTop:'10px'
    }
};

export default ConsultationListItem;
