import React, { Component } from 'react';
import './App.css';
import Router from 'next/router';
import SiteHeader from '../components/Common/SiteHeader';
import Link from 'next/link';


//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';


//import component
import CartList from "../components/Cart/CartList";
import CartClearSection from "../components/Cart/CartClearSection";
import CheckoutButton from "../components/Cart/CheckoutButton";
import Footer from "../components/Footer";
import Lang from '../lang/Lang';
import {Config} from "../Config";
import Colors from "../constants/Colors";

import CartService from "../service/CartService";
import ItemService from "../service/ItemService";

class cartPage extends Component {
    constructor(props){
        super(props);
        this.state = {
        };
    }

    getCart() {
        let companyId = s
        let params = s;
        ItemService.getItemDetail(companyId, params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    itemList: responseJson.children
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }


    render() {
        return (
            <div className="App">
                <SiteHeader/>
                <div className='App-body'>
                    <div style={styles.pageTitle}>{Lang.translate('CART')}</div>
                    <CartClearSection />
                    <CartList companyId='0f95b4d63b1afa75c3a9e2b3fae27cdf' />
                    <Footer/>
                    <CheckoutButton />
                </div>
            </div>
        );
    }
}const styles = {
    pageTitle: {
        fontSize: '22px',
        color: Colors.fontColor,
        textAlign: 'center',
        padding: '10px',
    }
};

export default cartPage;
