import React, {Component} from 'react';
import Colors from "../../../constants/Colors";
import ArticleContent from "./ArticleContent";
import ArticleComments from "./ArticleComments";
import UtilsService from "../../../service/UtilsService";
import AuthService from "../../../service/AuthService";

import EditButton from "../../../components/ItemDetails/ArticleDetails/EditButton"


class ArticleDetailComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLogin: true,
            item: this.props.item
        };
    }


    componentDidMount() {
        alert(this.state.isLogin);
        this.setState({
            isLogin: localStorage.getItem('isLogIn'),
        });


        if (this.state.item.is_draft === 1 && !this.state.isLogin) {
            this.weChatLogin();
        }
        else if (this.state.isLogin) {
            let userObj = localStorage.getItem('userObject');
            if (userObj.is_admin || userObj.adminOfCompanyIds.includes(this.state.item.company_id)) {
                this.setState({
                    isAdmin: true,
                })
            }
        }

    }

    weChatLogin() {
        let origUrl = window.location.href;
        // let authUrl = UtilsService.getWebAddress() + "AuthPage";
        let authUrl = "http://192.168.2.185:3000/AuthPage";
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }

    render(){
        const item = this.state.item ? this.state.item : undefined;
        const isLogin = this.state.isLogin;
        const isAdmin = this.state.isAdmin ? this.state.isAdmin : undefined;

        if (item.is_draft === 0 || (item.is_draft === 1 && isLogin)) {
            if (item.is_draft === 1 && !isAdmin) {
                return (
                    <div key={item.id}>
                        No Access to view this article.
                    </div>
                )
            } else {
                return (
                    <div key={item.id}>
                        <EditButton item={item}/>
                        <ArticleContent articleDetail={item} isAdmin={isAdmin}/>
                        <EditButton item={item}/>
                        <ArticleComments item={item}/>
                    </div>
                )
            }
        }
        else return null;
        // return (
        //     <div key={item.id}>
        //         <EditButton item={item}/>
        //         <ArticleContent articleDetail={item} isAdmin={isAdmin}/>
        //         <EditButton item={item}/>
        //         <ArticleComments item={item}/>
        //     </div>
        // )
    }
}

const styles = {

};

export default ArticleDetailComponent;
