import React, { Component } from 'react';
import './App.css';
import {Cells, Cell, CellBody, CellFooter, Gallery} from 'react-weui';
import ItemService from '../service/ItemService';
import JielongService from '../service/JielongService';
import SiteHeader from '../components/Common/SiteHeader';

//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';

//import component
import JielongContent from '../components/JieLongDetails/JieLongContent';
import Footer from "../components/Footer";
import JieLongBanner from "../components/JieLongDetails/JieLongBanner";
import JieLongButtonArea from "../components/JieLongDetails/JieLongButtonArea";
import JieLongPopUpMenu from "../components/JieLongDetails/JieLongPopUpMenu";
import JieLongRelatedProducts from "../components/JieLongDetails/JieLongRelatedProducts";
import Colors from "../constants/Colors";
import JieLongCompanyInfo from "../components/JieLongDetails/JieLongCompanyInfo";
import ProductButtonArea from "../components/ProductDetails/ProductButtonArea";
import CheckoutView from "../components/Cart/CheckoutView";
import ProductChildrenList from "../components/ProductDetails/ProductChildrenList";
import {Config} from "../Config";


import WechatService from "../service/WechatService";





class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            isLogin: false,
            showVoucher: false,
            itemType: 'product'
        };
    }

    static getInitialProps = async({ req, query }) => {
        let res = '';
        if (query.itemId) {
            res = await ItemService.getItemDetail(query.itemId);
        }
        else {
            // res = await ItemService.getItemDetail('a3067bcbd7521b4965ef8b43394aa011');
            res = await ItemService.getItemDetail('2b33c6d0b45d2170664a23866524a5c8');
        }
        const json = await res.json();
        return {item: json};
    };

    wechatShare() {
        var wx = require('weixin-js-sdk');

        console.log(wx);

        //判断是否为微信浏览器打开
        const ua = window.navigator.userAgent.toLowerCase();
        if (ua.indexOf('micromessenger') < 0) return false;


        console.log(window.location.href);

        WechatService.getConfig({'url': window.location.href})
            .then((response) => response.json())
            .then((responseJson) => {
                wx.config(responseJson.js);
            })
            .catch((error) => {
                console.error(error);
            });




        // wx.config({
        //     debug: true,
        //     appId: '',
        //     timestamp:'',
        //     nonceStr: '',
        //     signature: '',
        //     jsApiList: [
        //         'onMenuShareAppMessage',
        //         'onMenuShareTimeline'
        //     ]
        // })

        wx.ready(() => {
            //分享给朋友
            wx.onMenuShareAppMessage({
                title: 'test',
                desc: 'test',
                link: window.location.href,
                imgUrl: '',
                success: function (res) {

                },
                cancel: function (res) {

                }
            })

            //分享到朋友圈
            wx.onMenuShareTimeline({
                title: 'test',
                link:  window.location.href,
                imgUrl: '',
                success: function (res) {

                },
                cancel: function (res) {

                }
            })
        })

    }

    componentDidMount() {
        this.setState({
            isLogin: localStorage.getItem('isLogIn'),
        });
        this.getAllJielong();
        console.log('---------------');
        this.wechatShare();

    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item
        });
    }

    getAllJielong() {
        let id = this.state.item.id;
        const isLogIn = localStorage.getItem('isLogIn');
        if (isLogIn) {
            JielongService.getAllJielong(id)
                .then((response) => response.json())
                .then((responseJson) => {
                    let currentId = responseJson.length - 1;
                    this.setState({
                        allJielongInfo: responseJson,
                        processAllJieLong: true,
                        userJieLongInfo : responseJson[currentId],
                    });
                })
                .catch((error) => {
                    console.error(error);
                });
        }
    }

    showHideJieLongMenu() {
        this.setState((prevState) => ({
            iosShow: !prevState.iosShow
        }));
    };

    showHideCheckoutView = () => {
        this.setState((prevState) => ({
            isCheckingOut: !prevState.isCheckingOut
        }));
    };

    updateSelectedItems = (selectedList) => {
        this.setState({
            selectedList: selectedList && selectedList.length > 0 ? selectedList : false
        }, () => {
            console.log(this.state.selectedList);
        })
    };


    render() {
        const item = this.state.item ? this.state.item : undefined;
        const isLogin = this.state.isLogIn;

        // Jie Long Related Const
        const allJielongInfo = this.state.allJielongInfo ? this.state.allJielongInfo : undefined;
        const showLoading = this.state.showLoading ? this.state.showLoading : false;
        const userJieLongInfo = this.state.userJieLongInfo ? this.state.userJieLongInfo : '';
        const loadingJieLong = this.state.processAllJieLong;
        const iosShow = typeof this.state.iosShow !== 'undefined' ? this.state.iosShow : false;

        // Product Related Const
        const isCheckingOut = typeof this.state.isCheckingOut !== 'undefined' ? this.state.isCheckingOut : false;


        const itemDetailView = () => ((
            <div className='App-body' key={item.id}>

                <JieLongBanner item={item}/>
                <JielongContent jieLongDetail={item ? item : undefined}/>

                {/*<ChildrenList children={item.children ? item.children : undefined}/>*/}

                <ProductChildrenList children={item.children ? item.children : undefined} callbackUpdateItem={this.updateSelectedItems}/>

                <JieLongCompanyInfo item={item}/>

                <JieLongRelatedProducts companyId={item.company_id}/>

                { this.state.itemType === 'product'
                    ?
                    <ProductButtonArea item={item} isLogin={isLogin} selectedList={this.state.selectedList} callbackChangeView={this.showHideCheckoutView}  />
                    :
                    <div>
                        <JieLongButtonArea
                            item={item}
                            isLogin={isLogin}
                            userJieLongInfo={userJieLongInfo}
                            loadingJieLong={loadingJieLong}
                            showHideJieLongMenu={()=>this.showHideJieLongMenu()}/>
                        <JieLongPopUpMenu
                            item={item}
                            showHideJieLongMenu={()=>{this.showHideJieLongMenu()}}
                            showLoading={showLoading}
                            iosShow={iosShow}
                            userJieLongInfo={userJieLongInfo}/>
                    </div>
                }
            </div>
        ));

        return (
            <div className="App">
                <SiteHeader/>
                { isCheckingOut ? <CheckoutView selectedList={this.state.selectedList} parentItem={item}/> : itemDetailView()
                }
                <Footer/>
            </div>
        );
    }
}

const styles = {
    bottomDivider: {
        clear: 'both',
        padding: '3px 0',
        backgroundColor: Colors.borderColor,
        display: 'block'
    },
    galleryButton: {
        color: 'white',
        fontWeight:'bold',
        padding: '0',
    },
    relatedProductWrapper: {
        backgroundColor: '#fff',
        padding: '5vmin'
    },
    buttonGroupContainer: {
        position: 'fixed',
        left: 0,
        bottom: 0,
        width: '100%',
        backgroundColor: '#fff',
        color: '#fff',
        textAlign: 'center',
    }
};

export default App;
