import React, { Component } from 'react';
import './App.css';
import 'weui';
import 'react-weui/build/packages/react-weui.css';
import Colors from "../constants/Colors";
import Lang from '../lang/Lang';
//import component
import SiteHeader from '../components/Common/SiteHeader';
import CartClearSection from "../components/Cart/CartClearSection";
import Footer from "../components/Footer";
import CartService from "../service/CartService";
import CartListItem from "../components/Cart/CartListItem";
import CheckoutView from "../components/Cart/CheckoutView";


class cartPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            companyId: '8e26b6edb2ce7416f71a82ed78e08984',
            cartItems: [],
            selectedItemList: [],
            totalCost: 0,
            isCheckingOut: false
        };
    }

    componentDidMount() {
        this.getCartItems();
    }

    getCartItems() {
        let companyId = this.state.companyId;
        CartService.getCartItems(companyId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    cartItems: responseJson && responseJson[0] && responseJson[0].cartitems ? responseJson[0].cartitems : []
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    clearUserCart = () => {
        CartService.clearCartItems (this.state.companyId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    cartItems: [],
                    totalCost: 0,
                });
            })
            .catch((error) => {
                console.error(error);
            });
    };

    editSelectedCartItems = (itemId, qty) => {
        let selectedList = [];
        let tmpCost = 0;
        for (let cartItem of this.state.cartItems) {
            if (cartItem.item_id === itemId ){
                cartItem.qty = qty;
                selectedList.push(cartItem);
            }
        }
        this.setState({ selectedItemList: selectedList }, () => {
            for (let selectedItem of selectedList) {
                this.setState({totalCost: tmpCost + parseInt(selectedItem.qty) * 8})
            }
            this.updateUserCart(itemId, qty);
        });
        // totalCost: tmpCost + parseInt(selectedItem.qty) * parseInt(selectedItem.item.price)
    };

    updateUserCart = (itemId, qty) => {
        let params = {
            item_id: itemId,
            qty: qty
        };
        CartService.updateCartItems (this.state.companyId, params)
            .then((response) => response.json())
            .then((responseJson) => {
            })
            .catch((error) => {
                console.error(error);
            });
    };


    showHideCheckoutView = () => {
        this.setState({
            isCheckingOut: !this.state.isCheckingOut
        })
    };



    render() {
        const cartItems = this.state.cartItems;
        const editSelectedCartItems = this.editSelectedCartItems;
        const selectedItemList = this.state.selectedItemList;
        return (
            <div className="App">
                <SiteHeader/>


                <div>
                    <div style={styles.pageTitle}>{Lang.translate('CART')}</div>
                    <CartClearSection callbackClearCart={this.clearUserCart}/>
                    {cartItems.map (function (item,index) {return (<CartListItem item={item} key={index} callbackUpdate={editSelectedCartItems}/>)})}
                    <div style={styles.buttonWrapper}>
                        <div style={styles.title}>
                            总计：<span style={styles.price}>${this.state.totalCost}</span>
                        </div>
                        <button type="primary" style={styles.button} onClick={()=>{this.showHideCheckoutView()}}>{Lang.translate('CHECKOUT')}</button>
                    </div>
                </div>


                <CheckoutView itemList={selectedItemList}/>


                <Footer/>
            </div>
        );
    }
}
const styles = {
    pageTitle: {
        fontSize: '22px',
        color: Colors.fontColor,
        textAlign: 'center',
        padding: '10px',
    },
    buttonWrapper: {
        position: 'fixed',
        left: '0',
        bottom: '0',
        width: '100%',
        borderTop: '1px solid',
        borderTopColor: Colors.borderColor,
        textAlign: 'center',
        height: "40px",
        display: "flex",
        flexDirection: "row",
        color: Colors.fontColor,
        justifyContent: "center",
        alignItems: "center"
    },
    title: {
        flex: '5',
        fontWeight:"bold",
        fontSize: "18px",
    },
    button: {
        flex: '2',
        fontWeight: "bold",
        backgroundColor: Colors.appThemeColor,
        fontSize: "18px",
        height: "40px",
        borderWidth: "0",
    },
    price: {
        color:"red",
    }
};

export default cartPage;
