import React, {Component} from 'react';
import Colors from "../../../constants/Colors";
import ArticleContent from "./ArticleContent";
import ArticleComments from "./ArticleComments";
import {Config} from "../../../Config";
import AuthService from "../../../service/AuthService";


class ArticleDetailComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLogin: this.props.isLogin,
            item: this.props.item
        };
    }

    componentDidMount() {

    }

    weChatLogin() {
        console.log(window.location.href);
        // let origUrl = this.state.item.id;
        let origUrl = window.location.href;
        let authUrl = Config.webAddress + "AuthPage";
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }


    render(){
        const item = this.state.item ? this.state.item : undefined;
        const isLogin = this.state.isLogIn;
        console.log(item.is_published);

        if (item.is_published) {
            return (
                <div key={item.id}>
                    <ArticleContent articleDetail={item}/>
                    <ArticleComments item={item}/>
                </div>
            )
        }


        // return (
        //     <div key={item.id}>
        //         <ArticleContent articleDetail={item}/>
        //         <ArticleComments item={item}/>
        //     </div>
        // )
    }
}

