import React from 'react'
import { validateEmail, validateNum, validateDefaultInput } from '../../Helpers/FormValidation/InputValidation'

class Input extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            value: this.props.value ? this.props.value : '',
            isValid: this.props.value || this.props.value === '' ? true : undefined,
            type: this.props.type ? this.props.type : undefined,
            required: this.props.required ? this.props.required : false,
            errorMessage: this.props.errorMessage ? this.props.errorMessage : undefined,
            disabled: typeof this.props.disabled !== "undefined" ? this.props.disabled : false,
            placeholder: this.props.placeholder ? this.props.placeholder : null,
            onTextChange: this.props.onTextChange ? this.props.onTextChange : undefined,
            name: this.props.name ? this.props.name : undefined,
            numberLength: this.props.numberLength ? this.props.numberLength : undefined
        }
    }

    onBlur = () => {
        this.handleChange({
            target: { value: this.state.value, name: this.state.name }
        })
    }

    handleChange = ( event ) => {
        this.setState({ value: event.target.value }, ()=>{
            switch(this.state.type){
                case 'email':
                    this.setState({
                        isValid: validateEmail(this.state.value),
                    }, () => {
                        this.state.onTextChange(this.getEvent())
                    })
                    break

                case 'number':
                    this.setState({
                        isValid: validateNum(this.state.value, this.state.numberLength),
                    }, () => {
                        this.state.onTextChange(this.getEvent())
                    })
                    break

                default:
                    this.setState({
                        isValid: this.state.required ? validateDefaultInput(this.state.value) : true
                    }, () => {
                        this.state.onTextChange(this.getEvent())
                    })
                    break
            }
        })
    }

    getEvent = () => {
        let tmpEvent = {};
        tmpEvent.target = { isValid: this.state.isValid, name: this.state.name, value: this.state.value }
        return tmpEvent ;
    }

    render(){
        const { value, isValid, styles, type, required, errorMessage, disabled, placeholder, name } = this.state
        const err = isValid ? null : <p>{errorMessage}</p>
        return (
            <div>
                <p>{ name }</p>
                <input onBlur={this.onBlur} value={value} type={type} required={required} placeholder={placeholder} disabled={disabled} style={styles} onChange={this.handleChange} name={name}/>
                { err }
            </div>
        )
    }
}

export default Input
