import React from "react";


const globalState = {
    userToken: '',
    userId: '',
    user: {},
    isLogIn: false
};


export const StoreGlobal = (obj)=> {

    if (obj.type === 'set'){
        globalState[obj.key] = obj.value;
        return true;
    }
    else if (obj.type === 'get'){
        return globalState[obj.key];
    }
    else if (obj.type === 'erase'){
        globalState['userToken'] = '';
        globalState['userId'] = '';
        globalState['user'] = {};
        globalState['isLogIn'] = false;
        return true
    }
    else {
        return null;
    }

};
