import React, {Component} from 'react';
import Colors from "../../../constants/Colors";
import RegistrationFields from "./RegistrationFields";
import RegistrationButtonArea from "./RegistrationButtonArea";
import RegistrationVoucherComponent from "./RegistrationVoucherComponent";
import ItemBanner from "../ItemBanner";
import ItemContent from "../ItemContent";
import UtilsService from "../../../service/UtilsService";
import AuthService from "../../../service/AuthService";
import {Config} from "../../../Config";
import Input from "../../Common/form/Input";


class RegistrationDetailComponent extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLogin: this.props.isLogin,
            item: this.props.item,
            isParticipated: undefined,
            registrationId: undefined,
            formValues: {}
        };
    }

    componentDidMount() {
        // this.setState({
        //     isLogin: localStorage.getItem('isLogIn'),
        // }, () => {
        //     if (!this.state.isLogin) {
        //         this.weChatLogin();
        //     }
        // });
    }

    showHideCheckoutView = () => {
        this.setState((prevState) => ({
            isCheckingOut: !prevState.isCheckingOut
        }));
    };

    updateFormValues = formValues => {
        this.setState({
            formValues: formValues
        })
    };

    updateIsParticipatedInfo = (isParticipated , registrationId)=> {
        this.setState({
            isParticipated: isParticipated,
            registrationId: registrationId
        })
    }

    updateEditStatus = isEditing => {
        this.setState({
            isEditing: isEditing
        })
    }

    weChatLogin() {
        let origUrl = window.location.href;
        let authUrl = Config.webAddress +'AuthPage';
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }

    render(){
        const { item, isLogin, isParticipated, registrationId } = this.state
        return (
            <div className='App-body' key={ item.id } style={ styles.pageBackground }>
                <form>
                    <Input label="test field" errorMessage="error!!!" required={true} />
                </form>

                <ItemBanner item={ item }/>

                <RegistrationVoucherComponent item={ item } isLogin={ isLogin } callbackUpdateIsParticipated={this.updateIsParticipatedInfo} />

                <ItemContent itemDetail={ item ? item : undefined } showDivider={ false }/>

                <RegistrationFields item={ item } callbackUpdateForm={this.updateFormValues} isParticipated={ isParticipated } showDivider={true}/>

                <RegistrationButtonArea item={ item } isLogin={ isLogin } formValues={ this.state.formValues } isParticipated={ isParticipated } registrationId={ registrationId } />
            </div>

        )
    }
}

const styles = {
    pageBackground: {
        backgroundColor: Colors.formBackground
    }
};

export default RegistrationDetailComponent;
