import React, { Component } from 'react';
import './App.css';
import { Toast} from'react-weui';
import Router from 'next/router';


//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';


//import component
import Head from "../components/head";


class AuthPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            showToast: true,
        };
    }

    componentDidMount() {
        console.log(this.props);
        let itemId = {itemId: this.props.url.query.origUrl};
        localStorage.setItem('userToken', this.props.url.query.token);
        localStorage.setItem('ayth', this.props.url.query.token);
        localStorage.setItem('isLogIn', true);
        Router.push({
            pathname: '/checkoutPage',
            query: itemId,
        });
    }



    render() {
        return (
            <div className="App-body">
                <Head>
                    <meta name="viewport" content="initial-scale=1.0, width=device-width" />
                </Head>
                <header className="App-header">
                    <div>
                        ShowMine
                    </div>
                </header>
                <div className='App-body'>
                    <Toast icon="loading" show={this.state.showToast}>微信登录中...</Toast>
                </div>

            </div>

        );
    }
}

export default AuthPage;
