import React, { Component } from 'react'
import Colors from "../../../constants/Colors"
import { Button, TextArea, ActionSheet, Switch, Radio, Cells, CellsTitle, Cell, CellHeader, CellBody, CellFooter, Form, FormCell, Input, Label } from 'react-weui'

class RegistrationFields extends Component {
    constructor(props){
        super(props)
        this.state = {
            callbackUpdateForm: this.props.callbackUpdateForm,
            config: this.props.item.config,
            formValues: {},
            formArray: []
        }
    }

    componentDidMount() {
        this.extractFieldsFromConfig()
        this.initFormTemplate()
    }

    initFormTemplate = () => {
        let tmpConfig = JSON.parse(this.state.config)
        let tmpFormValues = {}
        for (let field of tmpConfig.inputFields) {
            tmpFormValues[field.title] = { [field.title]: '' }
        }
        this.setState({
            formValues: tmpFormValues
        })
    }

    extractFieldsFromConfig = () => {
        let tmpConfig = JSON.parse(this.state.config)
        this.setState({
            inputFields: tmpConfig.inputFields ? tmpConfig.inputFields : undefined
        })
    }

    handleChange = event => {
        let { formValues } = this.state
        formValues[event.target.name] = { [event.target.name]: event.target.value }
        this.setState({
            formArray: Object.values(formValues)
        }, ()=>{ this.state.callbackUpdateForm(this.state.formArray)
        })
    }


    render(){
        const { inputFields }= this.state
        if (inputFields && inputFields.length > 0) {
            return (
                <div style={styles.formWrapper}>
                    {
                        inputFields.map((field, index) => {
                            let { type, title } = field
                            return (
                                <div key={ index } >
                                    <CellsTitle>{ field.title }</CellsTitle>
                                    <Form>
                                        <FormCell>
                                            <CellBody>
                                                {
                                                    type === 'input' ?
                                                        <Input
                                                            type="text"
                                                            name={ title }
                                                            onChange={ this.handleChange }
                                                            placeholder={ '请输入' + title }
                                                            style={ styles.inputField }/>
                                                        :
                                                        <TextArea rows="3"
                                                                  name={ title }
                                                                  style={ styles.inputField }
                                                                  onChange={ this.handleChange }/>
                                                }
                                            </CellBody>
                                        </FormCell>
                                    </Form>
                                </div>
                            )
                        })
                    }
                </div>
            )
        }
        else return null
    }
}

const styles = {
    formWrapper: {
        backgroundColor: Colors.formBackground
    },
    inputField: {
        fontSize: '14px'
    }
}

export default RegistrationFields
