import React, { Component } from 'react';
import './App.css';
import 'weui';
import 'react-weui/build/packages/react-weui.css';
import Colors from "../constants/Colors";
import Lang from '../lang/Lang';
import {Config} from "../Config";
//import component
import SiteHeader from '../components/Common/SiteHeader';
import CartList from "../components/Cart/CartList";
import CartClearSection from "../components/Cart/CartClearSection";
import CheckoutButton from "../components/Cart/CheckoutButton";
import Footer from "../components/Footer";
import CartService from "../service/CartService";
import CartListItem from "../components/Cart/CartListItem";


class cartPage extends Component {
    constructor(props){
        super(props);
        this.state = {
            companyId: '8e26b6edb2ce7416f71a82ed78e08984',
            cartItems: [],
            totalCost: 0
        };
    }

    componentDidMount() {
        this.getCartItems();
    }

    getCartItems() {
        let companyId = this.state.companyId;
        CartService.getCartItems(companyId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    cartItems: responseJson && responseJson[0] && responseJson[0].cartitems ? responseJson[0].cartitems : []
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }
    updateTotalPrice = () => {
        console.log('in total cost');
    };

    clearUserCart = () => {
      this.setState({
          cartItems: []
      })
    };


    render() {
        const cartItems = this.state.cartItems;
        const updateTotalPrice = this.updateTotalPrice;
        return (
            <div className="App">
                <SiteHeader/>
                <div style={styles.pageTitle}>{Lang.translate('CART')}</div>

                <CartClearSection callbackClearCart={this.clearUserCart}/>

                {cartItems.map (function (item,index) {return (<CartListItem item={item} key={index} callbackUpdatePrice={updateTotalPrice}/>)})}

                <Footer/>
                <CheckoutButton />
            </div>
        );
    }
}
const styles = {
    pageTitle: {
        fontSize: '22px',
        color: Colors.fontColor,
        textAlign: 'center',
        padding: '10px',
    },
};

export default cartPage;
