import React, { Component } from 'react';
import './App.css';
import 'weui';
import 'react-weui/build/packages/react-weui.css';
import Colors from "../constants/Colors";
import Lang from '../lang/Lang';
import {Config} from "../Config";
//import component
import SiteHeader from '../components/Common/SiteHeader';
import CartList from "../components/Cart/CartList";
import CartClearSection from "../components/Cart/CartClearSection";
import CheckoutButton from "../components/Cart/CheckoutButton";
import Footer from "../components/Footer";


class cartPage extends Component {
    constructor(props){
        super(props);
        this.state = {
        };
    }

    clearCartItems = () => {
        this.setState({
            emptyCart: [],
        })
    }


    // updateTotalPrice()

    render() {
        return (
            <div className="App">
                <SiteHeader/>
                <div className='App-body'>
                    <div style={styles.pageTitle}>{Lang.translate('CART')}</div>
                    <CartClearSection clearCart={()=>this.clearCartItems()}/>
                    <CartList companyId='8e26b6edb2ce7416f71a82ed78e08984' emptyCart={this.state.emptyCart} />
                    <Footer/>
                    <CheckoutButton />
                </div>
            </div>
        );
    }
}const styles = {
    pageTitle: {
        fontSize: '22px',
        color: Colors.fontColor,
        textAlign: 'center',
        padding: '10px',
    }
};

export default cartPage;
