import React, { Component } from 'react';
import './App.css';
import {Cells, Cell, CellBody, CellFooter, Gallery} from 'react-weui';
import ItemService from '../service/ItemService';
import JielongService from '../service/JielongService';
import Router from 'next/router';
import SiteHeader from '../components/Common/SiteHeader';
import Link from 'next/link';

//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';

//import component
import Head from "../components/head";
import ChildrenList from '../components/JieLongDetails/ChildrenList';
import JielongContent from '../components/JieLongDetails/JieLongContent';
import Footer from "../components/Footer";
import Lang from '../lang/Lang';
import JieLongBanner from "../components/JieLongDetails/JieLongBanner";
import JieLongButtonArea from "../components/JieLongDetails/JieLongButtonArea";
import JieLongPopUpMenu from "../components/JieLongDetails/JieLongPopUpMenu";
import JieLongRelatedProducts from "../components/JieLongDetails/JieLongRelatedProducts";
import Colors from "../constants/Colors";
import JieLongCompanyInfo from "../components/JieLongDetails/JieLongCompanyInfo";


class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            isLogin: false,
            showVoucher: false,
        };
    }

    static getInitialProps = async({ req, query }) => {
        let res = '';
        if (query.itemId) {
            res = await ItemService.getItemDetail(query.itemId);
        }
        else {
            res = await ItemService.getItemDetail('0f95b4d63b1afa75c3a9e2b3fae27cdf');
        }
        const json = await res.json();
        return { item: json };
    };


    componentDidMount() {
        this.setState({
            isLogin: localStorage.getItem('isLogIn'),
        });
        this.getIsInCurrentJieLong();
        if (this.state.item.multi_participations) {
            this.getAllJielong();
        }
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item
        });
    }

    getIsInCurrentJieLong() {
        let id = this.state.item.id;
        const isLogIn = localStorage.getItem('isLogIn');
        if (isLogIn) {
            JielongService.getIsInCurrentJieLong(id)
                .then((response) => response.json())
                .then((responseJson) => {
                    this.setState({
                        userJieLongInfo: responseJson,
                        processInJieLong: true,
                    });
                })
                .catch((error) => {
                    console.error(error);
                });
        }
    }

    getAllJielong() {

        let id = this.state.item.id;
        const isLogIn = localStorage.getItem('isLogIn');
        if (isLogIn) {
            JielongService.getAllJielong(id)
                .then((response) => response.json())
                .then((responseJson) => {
                    this.setState({
                        allJielongInfo: responseJson,
                        processAllJieLong: true,
                    });
                })
                .catch((error) => {
                    console.error(error);
                });
        }
    }

    showHideMenu(){
        this.setState({
            iosShow: !this.state.iosShow,
        });
    }

    render() {
        const item = this.state.item ? this.state.item : undefined;
        const allJielongInfo = this.state.allJielongInfo ? this.state.allJielongInfo : undefined;
        const showLoading = this.state.showLoading ? this.state.showLoading : false;
        const iosShow = typeof this.state.iosShow !== 'undefined' ? this.state.iosShow : false;
        const userJieLongInfo = this.state.userJieLongInfo ? this.state.userJieLongInfo : '';
        const isLogin = this.state.isLogIn;
        const loadingJieLong = this.state.processAllJieLong !== this.state.processInJieLong;

        console.log(isLogin && allJielongInfo);

        return (
            <div className="App">
                <SiteHeader/>
                <div className='App-body' key={item.id}>
                    <JieLongBanner images={item.images}/>
                    <JielongContent jieLongDetail={item ? item : undefined}/>
                    {
                        isLogin && userJieLongInfo && userJieLongInfo.currentUserJieLong && userJieLongInfo.currentUserJieLong.voucher && userJieLongInfo.currentUserJieLong.voucher.refnum && !item.multi_participations ?
                            (
                                <Cells>
                                    <Cell href={userJieLongInfo.currentUserJieLong.voucher.img_url} access>
                                        <CellBody>
                                            <div className='isInJielong-row'>
                                                <div><span className='tag-icon'>参加成功</span> 回执码：<span className='Ref-num'>{userJieLongInfo.currentUserJieLong.voucher.refnum}</span></div>
                                            </div>
                                        </CellBody>
                                        <CellFooter/>
                                    </Cell>
                                </Cells>
                            )
                            : null
                    }
                    <Cells>
                        {
                            isLogin && allJielongInfo ?
                                allJielongInfo.map((jieLong) =>
                                    <div key={jieLong.id}>
                                        <Cell href={undefined} onClick={e=>this.setState({ showVoucher: true})} access>
                                            <CellBody>
                                                {
                                                    jieLong && jieLong.is_paid && jieLong.voucher && jieLong.voucher.receipt && jieLong.voucher.receipt.amount && jieLong.voucher.refnum ?
                                                        <div className='allJielongInfo'>
                                                            <div><span className='paidAmount-icon'>已支付：${jieLong.voucher.receipt.amount}</span> 回执码：<span className='Ref-num'>{jieLong.voucher.refnum}</span></div>
                                                        </div>
                                                        :
                                                        <div className='isInJielong-row'>
                                                            {
                                                                jieLong.voucher && jieLong.voucher.refnum ?
                                                                    <div><span className='tag-icon'>参加成功</span> 回执码：<span className='Ref-num'>{jieLong.voucher.refnum}</span></div>
                                                                    :
                                                                    <div>提示：您有订单未完成，请在30分钟内完成支付。</div>
                                                            }
                                                        </div>
                                                }
                                            </CellBody>
                                            <CellFooter/>
                                        </Cell>
                                        <Gallery src={jieLong.voucher && jieLong.voucher.img_url ? jieLong.voucher.img_url : undefined} show={this.state.showVoucher}>
                                            <div style={styles.galleryButton} onClick={e=>this.setState({ showVoucher: false})}>Cancel</div>
                                        </Gallery>
                                    </div>
                                )
                                :
                                null
                        }
                    </Cells>

                    <ChildrenList children={item.children ? item.children : undefined}/>

                    <JieLongCompanyInfo item={item}/>

                    <JieLongRelatedProducts companyId={item.company_id}/>

                    <JieLongButtonArea
                        item={item}
                        isLogin={isLogin}
                        userJieLongInfo={userJieLongInfo}
                        loadingJieLong={loadingJieLong}
                    />

                    <JieLongPopUpMenu
                        item={item}
                        showHideMenu={()=>{this.showHideMenu()}}
                        showLoading={showLoading}
                        iosShow={iosShow}
                    />

                </div>
                <Footer/>
            </div>
        );
    }
}

const styles = {
    bottomDivider: {
        clear: 'both',
        padding: '3px 0',
        backgroundColor: Colors.borderColor,
        display: 'block'
    },
    galleryButton: {
        color: 'white',
        fontWeight:'bold',
        padding: '0',
    },
    relatedProductWrapper: {
        backgroundColor: '#fff',
        padding: '5vmin'
    },
    buttonGroupContainer: {
        position: 'fixed',
        left: 0,
        bottom: 0,
        width: '100%',
        backgroundColor: '#fff',
        color: '#fff',
        textAlign: 'center',
    }
};

export default App;
