import React from 'react'
import { Dialog,} from "react-weui"
import UtilsService from '../../../service/UtilsService'
import Colors from '../../../constants/Colors'
import JielongService from "../../../service/JielongService"

import Btn from "../../../components/Btn"

class RegistrationButtonArea extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            item: this.props.item,
            formValues: this.props.formValues,
            isLoadingJieLong: false,
            disabled: this.props.disabled,
            // isLogin: this.props.isLogin,
            // isParticipated: this.props.isParticipated,
            // registrationId: this.props.registrationId,
            isLogin: true,
            isParticipated: true,
            registrationId: this.props.registrationId,
            showConfirmDialog: false,
            currentJieLong: {}
        }
    }

    componentWillReceiveProps(nextProps, nextContext) {
        // this.setState({
        //     item: nextProps.item,
        //     isLogin: nextProps.address,
        //     formValues: nextProps.formValues,
        //     isParticipated: nextProps.isParticipated,
        //     registrationId: nextProps.registrationId
        // })
    }

    createJielongComment = () => {
        this.setState({ isLoadingJieLong: true })
        let itemId = this.state.item.id
        let params = {
            'config': JSON.stringify(this.state.formValues),
            'comments': '',
            'quantity': 1,
            'state_redirect_obj': {
                url: UtilsService.getWebAddress(),
            },
            'wechat_auth_url': "https://www.showmine66.com/a/wu?domain=https%253A%252F%252Fwww.showmine66.com&origurl=https%253A%252F%252Fwww.showmine66.com%252Fma%252Fi%252Fdbfcefa936b4cd6d5709953b5ea12673%2523app-top",
            'item_url': "https://www.showmine66.com/ma/i/" + itemId,
            'voucher_base_url': "https://www.showmine66.com/ma/jl/vci/VOUCHERID",
            'is_private': false
        }

        JielongService.createJielongComment(itemId, params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({ currentJieLong: responseJson })
                this.generateVoucher(responseJson.id, this.state.item)
            })
            .catch((error) => {
                console.error(error)
            })
    }

    updateJielongComment = () => {
        this.setState({ isUpdating: true })
        // TODO change to jieLongId
        let jieLongId = this.state.registrationId
        let params = {
            'config': JSON.stringify(this.state.formValues),
            'comments': '',
            'quantity': 1,
            'state_redirect_obj': {
                url: UtilsService.getWebAddress(),
            },
            'wechat_auth_url': "https://www.showmine66.com/a/wu?domain=https%253A%252F%252Fwww.showmine66.com&origurl=https%253A%252F%252Fwww.showmine66.com%252Fma%252Fi%252Fdbfcefa936b4cd6d5709953b5ea12673%2523app-top",
            'item_url': "https://www.showmine66.com/ma/i/" + this.state.item.id,
            'voucher_base_url': "https://www.showmine66.com/ma/jl/vci/VOUCHERID",
            'is_private': false
        }

        JielongService.updateJielongComment(jieLongId, params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({ currentJieLong: responseJson })
                this.generateVoucher(responseJson.id, this.state.item)
            })
            .catch((error) => {
                console.error(error)
            })
    }

    cancelJielong() {
        this.showHideConfirmDialog()
        this.setState({ isCanceling: true })
        let jieLongId = this.state.registrationId
        let url = "https://www.showmine66.com/ma/i/" + this.state.item.id;
        JielongService.cancelJielong(jieLongId, url, {})
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({showLoading: false
                }, () =>{ window.location.reload();
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    generateVoucher = (jieLongId, item) => {
        let itemUrl = "https://www.showmine66.com/ma/i/" + item.id
        let params = {
            is_update: false,
            item_url: itemUrl,
            voucher_base_url: "https://www.showmine66.com/ma/jl/vci/VOUCHERID",
        }
        JielongService.generateVoucher(params, jieLongId)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    voucher: responseJson,
                    isLoadingJieLong: false,
                }, () => {
                    window.location.reload();
                })
            })
            .catch((error) => {
                console.error(error)
            })
    }

    showHideConfirmDialog = () => {
        this.setState((prevState) => (
            {
                showConfirmDialog: !prevState.showConfirmDialog
            })
        )
    }

    render(){
        const { isParticipated, updateJielong } = this.state;
        const confirmClearButtons= {
            title: '取消报名',
            buttons: [
                {
                    type: 'default',
                    label: '再想想',
                    onClick: () => this.showHideConfirmDialog()
                },
                {
                    type: 'primary',
                    label: '确定',
                    onClick: ()=>this.cancelJielong()
                }
            ]
        };
        if (typeof isParticipated === 'undefined') {
            return null
        } else if (isParticipated) {
            if (updateJielong) {
                return (
                    <div style={styles.buttonGroupContainer}>
                        <div style={styles.buttonArea}>
                            <Btn onClick={()=>this.updateJielongComment()} disabled={this.state.disabled} title='确认修改' loading={this.state.isUpdating} />
                            <Btn onClick={()=>{this.setState({updateJielong: false})}} disabled={this.state.disabled} title='取消修改' loading={this.state.isCanceling} />
                        </div>
                        <Dialog style={styles.dialog} type="ios" title={confirmClearButtons.title} buttons={confirmClearButtons.buttons} show={this.state.showConfirmDialog}>确定取消报名？</Dialog>
                    </div>
                )
            } else {
                return (
                    <div style={styles.buttonGroupContainer}>
                        <div style={styles.buttonArea}>
                            <Btn onClick={()=>{this.setState({updateJielong: true})}} disabled={this.state.disabled} title='修改报名' loading={this.state.isUpdating} />
                            <Btn onClick={()=>this.showHideConfirmDialog()} disabled={this.state.disabled} title='取消报名' loading={this.state.isCanceling} />
                        </div>
                        <Dialog style={styles.dialog} type="ios" title={confirmClearButtons.title} buttons={confirmClearButtons.buttons} show={this.state.showConfirmDialog}>确定取消报名？</Dialog>
                    </div>
                )
            }
        } else {
            return (
                <div style={ styles.buttonGroupContainer }>
                    <Btn onClick={()=>{ this.createJielongComment() }} disabled={this.state.disabled} title='立刻报名' loading={this.state.isLoadingJieLong}/>
                </div>
            )
        }
    }
}

const styles = {
    buttonGroupContainer: {
        position: 'fixed',
        left: 0,
        bottom: 0,
        width: '100%',
        color: '#fff',
        textAlign: 'center',
    },
    dialog: {
        color: Colors.fontColor
    },
    buttonArea: {
        display:'flex',
        flexDirection: 'row',
    }
}


export default RegistrationButtonArea
