import React from 'react';
import {Button} from "react-weui";
import Lang from '../../lang/Lang';
import Router from "next/dist/client/router";
import {Config} from "../../Config";
import AuthService from "../../service/AuthService";

class JieLongButtonArea extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            isLogin: this.props.isLogin,
            userJieLongInfo: this.props.userJieLongInfo,
            loadingJieLong: this.props.loadingJieLong,
            showHideMenu: this.props.showHideMenu
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item,
            isLogin: object.address,
            userJieLongInfo: object.userJieLongInfo,
            loadingJieLong: object.loadingJieLong
        })
    }

    checkLoginStatus() {
        if (this.state.isLogin === true) {
            Router.push({
                pathname: '/checkoutPage',
                query: {itemId: this.state.item.id},
            });
        }
        else {
            this.weChatLogin();
        }
    }

    weChatLogin() {
        let origUrl = this.state.item.id;
        let authUrl = Config.webAddress + "AuthPage";
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }

    render(){
        const isLogin = this.state.isLogin;
        const userJieLongInfo = this.state.userJieLongInfo;
        const loadingJieLong = this.state.loadingJieLong;
        console.log(isLogin);
        console.log(loadingJieLong);

        const canEdit = userJieLongInfo && userJieLongInfo.currentUserJieLong && !userJieLongInfo.currentUserJieLong.is_paid;
        return (
            <div style={styles.buttonGroupContainer}>
                {
                    canEdit
                        ?
                        <Button type="primary" onClick={()=>{this.state.showHideMenu()}}>{Lang.translate('ITEM.EDIT.JIELONG')}</Button>
                        :
                        <Button type="primary" disabled={loadingJieLong} onClick={()=>{this.checkLoginStatus()}}>{Lang.translate('ITEM.BUY_NOW')}</Button>
                }
            </div>
        );
    }
}


const styles = {
    buttonGroupContainer: {
        position: 'fixed',
        left: 0,
        bottom: 0,
        width: '100%',
        backgroundColor: '#fff',
        color: '#fff',
        textAlign: 'center',
    }
};


export default JieLongButtonArea;
