import React from 'react';
import {Button, ButtonArea} from "react-weui";
import Lang from '../../lang/Lang';
import Router from "next/dist/client/router";
import {Config} from "../../Config";
import AuthService from "../../service/AuthService";
import Colors from '../../constants/Colors';

class ProductButtonArea extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            isLogin: this.props.isLogin,
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item,
            isLogin: object.address,
        })
    }

    addToCart() {
        if (this.state.isLogin) {
            //TODO add to cart
        }
        else {
            this.weChatLogin();
        }
    }

    buyNow() {
        if (this.state.isLogin) {
            //TODO show checkout view
        }
        else {
            this.weChatLogin();
        }
    }

    weChatLogin() {
        let origUrl = this.state.item.id;
        let authUrl = Config.webAddress + "AuthPage";
        AuthService.wechatLogin(origUrl, authUrl, {})
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                alert(error);
            });
    }

    render(){
        return (
            <div style={styles.buttonGroupContainer}>
                <ButtonArea direction="horizontal">
                    <Button onClick={()=>{this.addToCart()}} style={styles.btn}>加入购物车</Button>
                    <Button onClick={()=>{this.buyNow()}} style={styles.btn}>立刻购买</Button>
                </ButtonArea>
            </div>
        );
    }
}


const styles = {
    buttonGroupContainer: {
        position: 'fixed',
        left: 0,
        bottom: 0,
        width: '100%',
    },
    btn: {
        backgroundColor: Colors.appThemeColor,
        color: Colors.fontColor,
        fontWeight: 'bold'
    },
};


export default ProductButtonArea;
