import React, { Component } from 'react';
import './App.css';
import {Button, Cells, Cell, CellBody, CellFooter, ActionSheet, Toast} from 'react-weui';
import ItemService from '../service/ItemService';
import CompanyService from '../service/CompanyService';
import Router from 'next/router';

//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';

//import component
import Swipe from '../components/Swipe'
import Head from "../components/head";
import ProductCardGrid from "../components/ProductCards/ProductCardGrid";
import ChildrenList from '../components/JieLongDetails/ChildrenList';
import JielongContent from '../components/JieLongDetails/JieLongContent';
import Footer from "../components/Footer";
import Lang from '../lang/Lang';
import CompanyBanner from '../components/Company/CompanyBanner';
import SiteHeader from '../components/Common/SiteHeader';
import CompanyTitle from '../components/Company/CompanyTitle';
import SlideableJieLongList from "../components/Company/SlideableList/SlideableJieLongList";



class CompanyDetails extends Component {
    constructor(props){
        super(props);
        this.state = {
            company: this.props.company,
            showLoading: false,
            isLogin: false,
        };
    }

    static getInitialProps = async({ req, query }) => {
        let res = {};
        if (query.companyId) {
            res = await CompanyService.getCompanyDetails(query.companyId);
        }
        else {
            res = await CompanyService.getCompanyDetails('8e26b6edb2ce7416f71a82ed78e08984');
        }
        const json = await res.json();
        return {company: json};
    };


    componentDidMount() {
        this.setState({
            isLogin: localStorage.getItem('isLogIn'),
        });
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item
        })
    }

    render() {
        const company = this.state.company ? this.state.company : undefined;
        return (
            <div style={styles.companyWrapper}>
                <SiteHeader/>
                <CompanyBanner companyId={company.id}/>
                <CompanyTitle company={company}/>
                <SlideableJieLongList companyId={company.id} isOngoing={true}/>
                <SlideableJieLongList companyId={company.id} isOngoing={false}/>
            </div>
        );
    }

}


const styles = {
    companyWrapper: {
        width: '100%'
    }
};


export default CompanyDetails;
