import React, { Component } from 'react';
import './App.css';
import {Button, Cells, Cell, CellBody, CellFooter, ActionSheet, Toast} from 'react-weui';
import DetailService from '../service/DetailService';
import JielongService from '../service/JielongService';
import AuthService from "../service/AuthService";
import Router from 'next/router';



//import styles
import 'weui';
import 'react-weui/build/packages/react-weui.css';


//import component
import Swipe from '../components/Swipe'
import Head from "../components/head";
import ProductCardGrid from "../components/ProductCards/ProductCardGrid";
import ChildrenList from '../components/JieLong/ChildrenList';
import JielongContent from '../components/JieLong/JieLongContent';
import Footer from "../components/Footer";
import Lang from '../lang/Lang';
import {Config} from "../Config";



class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
            productList: undefined,
            showLoading: false,
            userJieLongInfo: '',
            isLogin: false,
            iosShow: false,
            actions: [
                {
                    label: '取消',
                    onClick: this.hide.bind(this)
                }
            ],
        };
    }

    hide(){
        this.setState({
            iosShow: false,
        });
    }

    static getInitialProps = async({ req, query }) => {
        let res = '';
        if (query.itemId) {
            res = await DetailService.getDetail(query.itemId);
        }
        else {
            // res = await DetailService.getDetail('a3067bcbd7521b4965ef8b43394aa011');
            res = await DetailService.getDetail('0f95b4d63b1afa75c3a9e2b3fae27cdf');
        }
        const json = await res.json();
        return { item: json };
    };


    componentDidMount() {
        this.getIsInCurrentJieLong();
        if (this.state.item.multi_participations) {
            this.getAllJielong();
        }
        this.getRelatedProducts();
        this.setState({
            isLogin: localStorage.getItem('isLogIn'),
        });
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item
        })
    }

    getRelatedProducts() {
        let params = this.state.item.company_id + '?is_draft=0&is_sync_to_market=1&page=1&page-size=6';
        DetailService.getRelatedProducts(params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    productList: responseJson
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    shouldComponentUpdate(nextProps) {
        return this.state !== nextProps;
    }

    getIsInCurrentJieLong() {
        localStorage.setItem('userToken', '3fcb34cf22187d53c127a9b02b4f1184');
        localStorage.setItem('isLogIn', true);

        let id = this.state.item.id;
        JielongService.getIsInCurrentJieLong(id)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({
                    userJieLongInfo: responseJson,
                });
            })
            .catch((error) => {
                console.error(error);
            });
    }

    getAllJielong() {
        let id = this.state.item.id;
        const userToken = localStorage.getItem('userToken');
        if (userToken) {
            JielongService.getAllJielong(id)
                .then((response) => response.json())
                .then((responseJson) => {
                    this.setState({
                        allJielongInfo: responseJson,
                    });
                })
                .catch((error) => {
                    console.error(error);
                });
        }

    }

    cancelJielong() {
        this.hide();
        this.setState({showLoading: true});
        let id = this.state.userJieLongInfo.currentUserJieLong.id;
        let url = "https://www.showmine66.com/ma/i/" + this.state.item.id;
        let params ={};
        JielongService.cancelJielong(id, url, params)
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({showLoading: false});
                window.location.reload();
            })
            .catch((error) => {
                console.error(error);
            });
    }

    updateJielong() {
        let itemId = { itemId: this.state.item.id, is_update: true, jielongId: this.state.userJieLongInfo.currentUserJieLong.id};
        Router.push({
            pathname: '/checkoutPage',
            query: itemId,
        });
    }

    checkLoginStatus() {
        let itemId = { itemId: this.state.item.id};
        let login = localStorage.getItem('isLogIn');
        if (login) {
            Router.push({
                pathname: '/checkoutPage',
                query: itemId,
            });
        }
        else {
            this.weChatLogin();
        }
    }

    weChatLogin() {
        let origUrl = this.state.item.id;
        // let authUrl = "http://localhost:3000/AuthPage";
        let authUrl = Config.webAddress + "AuthPage";
        let params ={};
        AuthService.wechatLogin(origUrl, authUrl, params)
            .then((response) => response.json())
            .then((responseJson) => {
                window.open(responseJson.auth_url, "_self");
            })
            .catch((error) => {
                console.error(error);
            });
    }

    render() {
        const userJieLongInfo = this.state.userJieLongInfo;
        const productList = this.state.productList && this.state.productList.data ? this.state.productList.data : undefined;
        const item = this.state.item ? this.state.item : undefined;
        const allJielongInfo = this.state.allJielongInfo ? this.state.allJielongInfo : undefined;
        return (
            <div className="App">
                <Head>
                    <meta name="viewport" content="initial-scale=1.0, width=device-width" />
                </Head>
                <header className="App-header">
                    <div onClick={() => Router.push('/loginPage')}>
                        {Lang.translate('SHOWMINE')}
                    </div>
                </header>
                <div className='App-body'>
                    <div className='content'>
                        <div key={item.id}>
                            <Swipe image={item.images}/>
                        </div>
                        <JielongContent jieLongDetail={item ? item : undefined}/>
                    </div>
                    {
                        this.state.isLogin && userJieLongInfo && userJieLongInfo.currentUserJieLong && userJieLongInfo.currentUserJieLong.voucher && userJieLongInfo.currentUserJieLong.voucher.refnum && !item.multi_participations ?
                            (
                                <Cells>
                                    <Cell href={userJieLongInfo.currentUserJieLong.voucher.img_url} access>
                                        <CellBody>
                                            <div className='isInJielong-row'>
                                                <div><span className='tag-icon'>参加成功</span> 回执码：<span className='Ref-num'>{userJieLongInfo.currentUserJieLong.voucher.refnum}</span></div>
                                            </div>
                                        </CellBody>
                                        <CellFooter/>
                                    </Cell>
                                </Cells>
                            )
                            : null
                    }
                    <Cells>
                        {
                            this.state.isLogin && allJielongInfo ?
                                allJielongInfo.map((jieLong) =>
                                    <Cell key={jieLong.id} href={jieLong.voucher && jieLong.voucher.img_url ? jieLong.voucher.img_url : undefined} access>
                                        <CellBody>
                                            {
                                                jieLong && jieLong.is_paid && jieLong.voucher && jieLong.voucher.receipt && jieLong.voucher.receipt.amount && jieLong.voucher.refnum ?
                                                    <div className='allJielongInfo'>
                                                        <div><span className='paidAmount-icon'>已支付：${jieLong.voucher.receipt.amount}</span> 回执码：<span className='Ref-num'>{jieLong.voucher.refnum}</span></div>
                                                    </div>
                                                    :
                                                    <div className='isInJielong-row'>
                                                        {
                                                            jieLong.voucher && jieLong.voucher.refnum ?
                                                                <div><span className='tag-icon'>参加成功</span> 回执码：<span className='Ref-num'>{jieLong.voucher.refnum}</span></div>
                                                                :
                                                                <div>提示：请在30分钟内完成支付，否则订单会撤销</div>
                                                        }
                                                    </div>
                                            }
                                        </CellBody>
                                        <CellFooter/>
                                    </Cell>
                                )
                                :
                                null
                        }
                    </Cells>
                    <ChildrenList children={item.children ? item.children : undefined}/>
                    {item.company ?
                        (
                            <div>
                                <div className='company-info'>
                                    <div className='company-img'>
                                        <img height={100} src={item.company.logo_img.url} role="presentation" />
                                    </div>
                                    <div className='company-content'>
                                        <div className='company-title'>{item.company.name}</div>
                                        <div className='company-followers'>关注量：{item.company.followers}</div>
                                    </div>
                                </div>
                                <div className='divider'/>
                            </div>)
                        :
                        null
                    }

                    <div className='related-product'>
                        <ProductCardGrid productList={productList}/>
                    </div>
                    <Footer/>
                    <div className='button-area'>
                        {this.state.isLogin && userJieLongInfo && userJieLongInfo.currentUserJieLong && !userJieLongInfo.currentUserJieLong.is_paid ?
                            (
                                <div style={{color:'black'}}>
                                    <Button type="primary" onClick={e=>this.setState({iosShow: true})}>修改订单</Button>
                                    <ActionSheet
                                        menus={[{
                                            label: '修改团购',
                                            onClick: ()=> {this.updateJielong();}
                                        }, {
                                            label: '取消团购',
                                            onClick: ()=> {this.cancelJielong();}
                                        }]}
                                        actions={this.state.actions}
                                        show={this.state.iosShow}
                                        type="ios"
                                        onRequestClose={e=>this.setState({iosShow: false})}
                                    />
                                    <Toast icon="loading" show={this.state.showLoading}>Loading...</Toast>
                                </div>
                            )
                            :
                            <Button type="primary" className='button' onClick={()=>{this.checkLoginStatus();}}>马上下单</Button>
                        }
                    </div>
                </div>
            </div>

        );
    }
}

export default App;
