import React from 'react';
import Lang from '../../lang/Lang';
import Colors from '../../constants/Colors';
import Link from 'next/link';

class JieLongCompanyInfo extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            item: this.props.item,
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            item: object.item,
        })
    }

    render(){
        const company = this.state.item && this.state.item.company && this.state.item.company.id ? this.state.item.company : undefined;
        if (company) {
            return (
                <div>
                    <Link href={{ pathname: '/companyDetails', query: { companyId: company.id} }}>
                        <a>
                            <div style={styles.companyWrapper}>
                                <div style={styles.companyLogoContainer}>
                                    <img style={styles.companyLogo} src={company.logo_img.url} role="presentation" />
                                </div>
                                <div style={styles.companyContent}>
                                    <div style={styles.companyTitle}>{company.name}</div>
                                    <div style={styles.followers}>{Lang.translate('ITEM.COMPANY.FOLLOWERS')}ï¼š{company.followers}</div>
                                </div>
                            </div>
                        </a>
                    </Link>
                    <div style={styles.bottomDivider}/>
                </div>
            )
        }
        else return null;
    }
}


const styles = {
    companyWrapper: {
        display: 'flex',
        flexDirection: 'row',
        padding: '5vmin',
        flex: '1',
        color: Colors.fontColor,
        backgroundColor: '#fff',
        justifyContent: 'center',
        alignItems: 'center'
    },
    companyLogoContainer: {
        flex:1
    },
    companyLogo: {
        width: '25vw',
        height: '25vw',
        justifyContent: 'center',
        alignItems: 'center'
    },
    companyContent: {
        display: 'flex',
        flexDirection: 'column',
        flex: '2'
    },
    companyTitle: {
        fontSize: '18px',
        fontWeight: 'bold',
        color: '#107fc4',
        textAlign: 'center'
    },
    followers: {
        fontSize: '12px',
        textAlign: 'end',
        fontWeight: 'bold',
        paddingRight: '10vw'
    },
    bottomDivider: {
        clear: 'both',
        padding: '3px 0',
        backgroundColor: Colors.borderColor,
        display: 'block'
    }
};


export default JieLongCompanyInfo;
