import React from 'react';
import {ActionSheet, Toast} from "react-weui";
import Lang from '../../lang/Lang';

class JieLongPopUpMenu extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            showLoading: this.props.showLoading,
            updateJieLong: this.props.updateJieLong,
            cancelJieLong: this.props.cancelJieLong,
            iosShow: this.props.iosShow,
            actions: [{
                    label: Lang.translate('CANCEL'),
                    onClick: ()=>{this.setState({iosShow: false})}
                }]
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            showLoading: object.showLoading,
        })
    }

    render(){
        const showLoading = this.state.showLoading;
        return (
            <div>
                <ActionSheet
                    menus={[{
                        label: Lang.translate('ITEM.EDIT.JIELONG'),
                        onClick: ()=> {this.state.updateJieLong()}
                    }, {
                        label: Lang.translate('ITEM.CANCEL.JIELONG'),
                        onClick: ()=> {this.state.cancelJieLong()}
                    }]}
                    actions={this.state.actions}
                    show={this.state.iosShow}
                    type="ios"
                    onRequestClose={()=>this.setState({iosShow: false})}
                    style={styles.menuFont}
                />
                <Toast icon="loading" show={showLoading}>{Lang.translate('LOADING')}...</Toast>
            </div>
        );
    }
}


const styles = {
    menuFont: {
        color: '#000'
    }
};

export default JieLongPopUpMenu;
