import React, {Component} from 'react';
import Iframe from 'react-iframe';
import {Button, Dialog, LoadMore, Toast} from "react-weui";
import Colors from "../constants/Colors";

class ButtonComponent extends Component {
    constructor(props){
        super(props);

        this.state = {
            style: this.props.style,
            disabled: this.props.disabled,
            loading: this.props.loading,
            title: this.props.title,
            onClick: this.props.onClick,
        };
    }

    componentWillReceiveProps(object, nextProps) {
        this.setState({
            style: object.style,
            disabled: object.disabled,
            loading: object.loading,
            title: object.title,
            onClick: object.onClick,
        })
    }

    render(){
        if (!this.state.loading){console.log('===================')}
        return (
            <div style={styles.buttonGroupContainer}>
                <Button style={this.state.style} onClick={this.state.onClick} disabled={this.state.disabled}>{this.state.loading ? <LoadMore loading/> : this.state.title}</Button>
                <Toast icon="loading" show={this.state.loading}>请稍后...</Toast>
            </div>
        );
    }
}

const styles = {
    dialog: {
        color: Colors.fontColor
    }
};

export default ButtonComponent;
